public class Programa06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int key = 5;

		switch (key) {
		case 1:
			System.out.println("numero é 1");

			break;
		case 2:
			System.out.println("Numero e 2");
		case 5:
			System.out.println("Numero é 5");
			break;

		default:
			System.out.println("Numero e :"+ key);
			break;
		}

	}

}
